import { useState } from "react";
import { Link } from "react-router-dom";

const Footer = () => {
   const [active, setActive] = useState("");
   return (
      <div
         id="footer"
         className="d-flex
          justify-content-center gap-5 m-0 border-top py-2 text-center bg-light"
      >
         <Link
            className={`d-flex flex-column text-decoration-none ${
               active ? "text-danger" : "text-black-50"
            }`}
         >
            <i class={`bi bi-heart${active && "-fill"} fs-3`}></i>
            <span>Favorites</span>
         </Link>
         <Link className="d-flex flex-column text-black-50 text-decoration-none">
            <i class="bi bi-heart-fill fs-3 "></i>
            <span>Favorites</span>
         </Link>
         <Link className="d-flex flex-column text-black-50 text-decoration-none">
            <i class="bi bi-heart-fill fs-3 "></i>
            <span>Favorites</span>
         </Link>
      </div>
   );
};

export default Footer;
