import { validateFormikUsingJoi } from "../utils/validateFormikUsingJoi";
import Input from "./common/input";
import PageHeader from "./common/pageHeader";

import { Navigate, useNavigate } from "react-router-dom";

import { useFormik } from "formik";
import Joi from "joi";
import { useState } from "react";
import { useAuth } from "../contexts/auth.context";

const SignUp = ({ redirect }) => {
   const [serverError, setServerError] = useState("");
   const navigate = useNavigate();

   const { user, signUp } = useAuth();

   const form = useFormik({
      validateOnMount: true,
      initialValues: {
         name: {
            first: "",
            middle: "",
            last: "",
         },
         phone: "",
         email: "",
         password: "",
         image: {
            url: "",
            alt: "",
         },
         address: {
            state: "",
            country: "",
            city: "",
            street: "",
            houseNumber: undefined,
            zip: undefined,
         },
         isBusiness: false,
      },
      validate: validateFormikUsingJoi({
         name: {
            first: Joi.string().min(2).max(255).required().label("first"),
            middle: Joi.string().min(2).max(255).label("middle"),
            last: Joi.string().min(2).max(255).required().label("last"),
         },
         phone: Joi.string()
            .min(9)
            .max(10)
            .required()
            .regex(/^0[2-9]\d{7,8}$/),
         email: Joi.string()
            .min(2)
            .max(255)
            .required()
            .email({ tlds: { allow: false } }),
         password: Joi.string()
            .min(9)
            .required()
            .regex(
               /^(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.*[!@#$%^&*-])(?=.{8,})/
            )
            .message(
               "'Password' must be at least 9 characters long and contain an uppercase letter, a lower case letter, a number and one of the following characters !@#$%^&*- "
            ),
         image: {
            url: "",
            alt: "",
         },
         address: {
            state: Joi.string().min(2).max(50),
            country: Joi.string().min(2).max(50).required(),
            city: Joi.string().min(2).max(50).required(),
            street: Joi.string().min(2).max(255).required(),
            houseNumber: Joi.number().min(1).max(3).required(),
            zip: Joi.string().min(2).max(10).required(),
         },
         isBusiness: false,
      }),
      async onSubmit(values) {
         try {
            await signUp({ ...values, biz: false });
            if (redirect) {
               navigate(redirect);
            }
         } catch (err) {
            if (err.response?.status === 400) {
               setServerError(err.response.data);
            }
         }
      },
   });

   if (user) {
      return <Navigate to={"/"} />;
   }

   console.log(form);

   return (
      <>
         <PageHeader
            title="Sign Up"
            description="Create an account and join our community! "
         />

         <form onSubmit={form.handleSubmit}>
            {serverError && (
               <div className="alert alert-danger">{serverError}</div>
            )}
            <div className="row row-cols-2">
               <Input
                  {...form.getFieldProps("name.first")}
                  type="text"
                  label="First name"
                  required
                  error={form.touched.name?.first && form.errors.first}
               />
               <Input
                  {...form.getFieldProps("name.middle")}
                  type="text"
                  label="middle name"
                  error={form.touched.name?.middle && form.errors.middle}
               />{" "}
               <Input
                  {...form.getFieldProps("name.last")}
                  type="text"
                  label="Last name"
                  required
                  error={form.touched.name?.last && form.errors.last}
               />
               <Input
                  {...form.getFieldProps("phone")}
                  type="string"
                  label="Phone"
                  required
                  error={form.touched.name && form.errors.name}
               />
               <Input
                  {...form.getFieldProps("email")}
                  type="email"
                  label="Email"
                  required
                  error={form.touched.email && form.errors.email}
               />
               <Input
                  {...form.getFieldProps("password")}
                  type="password"
                  label="Password"
                  required
                  error={form.touched.password && form.errors.password}
               />
            </div>

            <div className="row row-cols-2 my-5">
               <Input
                  {...form.getFieldProps("url")}
                  type="text"
                  label="Image url"
                  error={form.touched.name && form.errors.name}
               />
               <Input
                  {...form.getFieldProps("alt")}
                  type="text"
                  label="Image alt"
                  error={form.touched.name && form.errors.name}
               />
            </div>
            <div className="row row-cols-2">
               <Input
                  {...form.getFieldProps("state")}
                  type="text"
                  label="State"
                  error={form.touched.name && form.errors.name}
               />
               <Input
                  {...form.getFieldProps("country")}
                  type="text"
                  label="Country"
                  required
                  error={form.touched.name && form.errors.name}
               />
               <Input
                  {...form.getFieldProps("city")}
                  type="text"
                  label="City"
                  required
                  error={form.touched.name && form.errors.name}
               />
               <Input
                  {...form.getFieldProps("street")}
                  type="text"
                  label="Street"
                  required
                  error={form.touched.name && form.errors.name}
               />
               <Input
                  {...form.getFieldProps("house number")}
                  type="number"
                  label="House number"
                  required
                  error={form.touched.name && form.errors.name}
               />
               <Input
                  {...form.getFieldProps("zip")}
                  type="number"
                  label="Zip"
                  required
                  error={form.touched.name && form.errors.name}
               />
            </div>
            <div className="text-center my-2">
               <button disabled={!form.isValid} className="btn btn-primary">
                  Sign up
               </button>
            </div>
         </form>
      </>
   );
};

export default SignUp;
