export const useForm = ({ initialValue = {}, onSubmit = () => {} }) => {
   const [inputs, setInputs] = useState(initialValue);
   const handleChange = (e) => {
      setInputs({ ...inputs, [e.target.name]: e.target.value });
   };

   const handleSubmit = (e) => {
      e.preventDefault();

      onSubmit(inputs);
   };

   return {
      inputs,
      handleChange,
      handleSubmit,
   };
};
