import { useState } from "react";
import { Link, NavLink } from "react-router-dom";

const Footer = () => {
   const [active, setActive] = useState({
      about: "",
      favorites: "",
      myCards: "",
   });
   return (
      <div
         id="footer"
         className="d-flex
          justify-content-center gap-5 m-0 border-top py-2 text-center bg-light"
      >
         <NavLink
            to="/favorite-cards"
            className={({ isActive }) => console.log(isActive)}
            data-name="favorite"
         >
            <i className={`bi bi-heart${active && "-fill"} fs-3`}></i>
            <span>Favorites</span>
         </NavLink>
         <Link className="d-flex flex-column text-black-50 text-decoration-none">
            <i className="bi bi-heart-fill fs-3 "></i>
            <span>Favorites</span>
         </Link>
         <Link className="d-flex flex-column text-black-50 text-decoration-none">
            <i className="bi bi-heart-fill fs-3 "></i>
            <span>Favorites</span>
         </Link>
      </div>
   );
};

export default Footer;
