import PageHeader from "./common/pageHeader";
import { Link } from "react-router-dom";
import Card from "./card";
import { useMyCards } from "../hooks/useMyCards";

const MyCards = ({ search = "" }) => {
   const cards = useMyCards();

   function displayCards() {
      if (search) {
         return cards
            .filter((card) => card.title.includes(search))
            .map((card) => {
               return <Card card={card} key={card._id} />;
            });
      }
      return cards.map((card) => <Card card={card} key={card._id} />);
   }

   return (
      <>
         <PageHeader
            title="Welcome"
            description={
               <>
                  Here are the cards our users created, like what you see?{" "}
                  <span>join us</span>
               </>
            }
         />

         <div className="row mt-3 gap-3">
            {!cards.length ? <p>no cards...</p> : displayCards()}
         </div>
      </>
   );
};

export default MyCards;
