import { validateFormikUsingJoi } from "../utils/validateFormikUsingJoi";
import Input from "./common/input";
import PageHeader from "./common/pageHeader";

import { Navigate, useNavigate } from "react-router-dom";

import { useFormik } from "formik";
import Joi from "joi";
import { useState } from "react";
import { useAuth } from "../contexts/auth.context";

const SignUp = ({ redirect }) => {
   const [serverError, setServerError] = useState("");
   const navigate = useNavigate();

   const { user, signUp } = useAuth();

   const form = useFormik({
      validateOnMount: true,
      initialValues: {
         name: {
            first: "",
            middle: "",
            last: "",
         },
         phone: "",
         email: "",
         password: "!123Abc",
         image: {
            url: "",
            alt: "",
         },
         address: {
            state: "",
            country: "",
            city: "",
            street: "",
            houseNumber: undefined,
            zip: undefined,
         },
         isBusiness: false,
      },
      validate: validateFormikUsingJoi({
         name: {
            first: Joi.string().min(2).max(255).required(),
            middle: Joi.string().min(2).max(255),
            last: Joi.string().min(2).max(255).required(),
         },
         phone: "",
         email: "",
         password: Joi.string()
            .min(9)
            .max(10)
            .required()
            .regex(
               /^(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.*[!@#$%^&*])(?=.{8,})/
            ),
         image: {
            url: "",
            alt: "",
         },
         address: {
            state: Joi.string().min(2).max(255).required(),
            country: Joi.string().min(2).max(255).required(),
            city: Joi.string().min(2).max(255).required(),
            street: Joi.string().min(2).max(255).required(),
            houseNumber: undefined,
            zip: undefined,
         },
         isBusiness: false,
         name: Joi.string().min(2).max(255).required(),
         email: Joi.string()
            .min(2)
            .max(255)
            .required()
            .email({ tlds: { allow: false } }),
         password: Joi.string().min(6).max(1024).required(),
      }),
      async onSubmit(values) {
         try {
            await signUp({ ...values, biz: false });
            if (redirect) {
               navigate(redirect);
            }
         } catch (err) {
            if (err.response?.status === 400) {
               setServerError(err.response.data);
            }
         }
      },
   });

   if (user) {
      return <Navigate to={"/"} />;
   }

   return (
      <>
         <PageHeader
            title="Sign Up"
            description="Open a new account now!!! It is free you yammani!!"
         />

         <form onSubmit={form.handleSubmit}>
            {serverError && (
               <div className="alert alert-danger">{serverError}</div>
            )}

            <Input
               {...form.getFieldProps("email")}
               type="email"
               label="Email"
               required
               error={form.touched.email && form.errors.email}
            />
            <Input
               {...form.getFieldProps("name")}
               type="text"
               label="Name"
               required
               error={form.touched.name && form.errors.name}
            />
            <Input
               {...form.getFieldProps("password")}
               type="password"
               label="Password"
               required
               error={form.touched.password && form.errors.password}
            />

            <div className="my-2">
               <button disabled={!form.isValid} className="btn btn-primary">
                  Sign Up
               </button>
            </div>
         </form>
      </>
   );
};

export default SignUp;
