import { useState } from "react";
import { useAuth } from "../contexts/auth.context";

export function useFavoriteCards() {
   const [favorite, setFavorite] = useState(false);
   const { user } = useAuth();
   if (likes.includes(user._id) && !favorite) {
      setFavorite((favorite) => true);
   }

   async function handleAddFavorite(cardId) {
      debugger;
      const response = await addCardToFavorites(cardId);
      setFavorite((favorite) => !favorite);
      console.log(response);
   }
   return { favorite, handleAddFavorite };
}
