import { NavLink } from "react-router-dom";

const FooterIcon = ({
   title = "",
   href = "/",
   bsColorWhenActive = "primary",
   icon = "",
}) => {
   return (
      <NavLink
         to={href}
         className={({ isActive }) =>
            `d-flex flex-column text-decoration-none ${
               isActive ? `text-${colorWhenActive}` : "text-black-50"
            }`
         }
      >
         {({ isActive }) => (
            <>
               <i
                  className={`bi bi-${icon}${isActive ? "-fill" : ""} fs-3`}
               ></i>
               <span>Favorites</span>
            </>
         )}
      </NavLink>
   );
};

export default FooterIcon;
