import { useAuth } from "../contexts/auth.context";
import Card from "./card";
import PageHeader from "./common/pageHeader";

const FavCards = ({ allCards, search = "" }) => {
   const { user } = useAuth();

   function displayCards() {
      const favorites = allCards.filter((card) => card.user_id === user._id);
      if (search) {
         return allCards.map((card) => {
            return <Card card={card} key={card._id} />;
         });
      }
      favorites.length ? (
         favorites.map((card) => <Card card={card} key={card._id} />)
      ) : (
         <div>No Favs</div>
      );
      return allCards.map((card) => <Card card={card} key={card._id} />);
   }

   return (
      <>
         <PageHeader title={"Favorite cards"} />
         <div className="row">{displayCards()}</div>
      </>
   );
};

export default FavCards;
