import { useState } from "react";

export const useColorMode = () => {
   const [colorMode, setColorMode] = useState("dark");

   function switchColorMode() {
      setColorMode((colorMode) => (colorMode === "light" ? "dark" : "light"));
      document.querySelector("body").setAttribute("data-bs-theme", colorMode);
      if (colorMode === "dark") {
         return document.querySelector("nav").classList.toggle("bg-light");
      }
      document.querySelector("nav").classList.toggle("bg-dark");
   }

   return {
      colorMode,
      switchColorMode,
   };
};
